package day2;

public class Ex8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean a, b, c;
		
		a = true; 
		b = false;
		
		c = a & b; //AND
		System.out.println(c);
		
		c = a | b; //OR
		System.out.println(c);
		
		c = !b; //NOT
		System.out.println(c);
		
		c = a ^ b; //EX OR
		System.out.println(c);
		
	}

}
